import React, { useState } from 'react'
import { update } from "../../BooksAPI"

const BookCard = ({
    allBooks,
    bookInfo,
    changeBookStatus,
    setAllBooks
}) => {
    const [ status, setStatus ] = useState(bookInfo.shelf)

    const handleClick = (e) => {
      changeBookStatus(bookInfo, e.target.value)
      update(bookInfo, e.target.value)
        .then((res) => {
          console.log('res', res)
          setStatus(e.target.value)
          setAllBooks({
            ...allBooks,
            ...bookInfo
          })
        })
        .catch((err) => console.error(err))
    }

    return (
        <li>
        <div className="book">
          <div className="book-top">
            <div
              className="book-cover"
              style={{
                width: 128,
                height: 193,
                backgroundImage:
                  `url(${bookInfo.imageLinks.thumbnail})`,
              }}
            ></div>
            <div className="book-shelf-changer">
              <select value={status} onChange={handleClick}>
                <option value="none" disabled>
                  Move to...
                </option>
                <option value="currentlyReading">
                  Currently Reading
                </option>
                <option value="wantToRead">Want to Read</option>
                <option value="read">Read</option>
                <option value="none">None</option>
              </select>
            </div>
          </div>
          <div className="book-title">{bookInfo.title}</div>
          <div className="book-authors">{bookInfo?.authors[0]}</div>
        </div>
      </li>
    )
}

export default BookCard;
